INSERT INTO epigraphs (quote, author) VALUES
('Выживание можно выразить в трех словах: никогда не сдавайся. В этом вся суть. Просто продолжай пытаться', 'Беар Гриллс'),
('Жизнь — это не просто быть живым, а быть сильным', 'Джек Лондон'),
('Человек рожден не для поражения. Человека можно уничтожить, но его нельзя победить', 'Эрнест Хемингуэй'),
('В дикой природе нет ничего лишнего. Всё существует с одной целью — выжить', 'Джон Мьюир'),
('Лес не прощает ошибок, но учит видеть главное', 'Михаил Пришвин'),
('Когда ты один в лесу, слышишь не тишину, а себя', 'Генри Дэвид Торо'),
('Пока ты дышишь — ты борешься. Пока борешься — живёшь', 'Лоран Гунель'),
('Природа — лучший учитель. Она строга, но справедлива', 'Рэй Мирз'),
('Страх — это естественно. Главное — не позволить ему остановить тебя', 'Стивен Каллахэн'),
('Каждая ночь в лесу — как экзамен на человечность', 'Николай Задорнов'),
('Ты не победишь бурю. Ты можешь только пройти сквозь неё', 'Антуан де Сент-Экзюпери'),
('Только теряя всё, человек начинает по-настоящему ценить жизнь', 'Ричард Проктор'),
('В тайге нет победителей. Здесь есть только те, кто остался жив', 'Виталий Сундаков'),
('Природа не жестока — она равнодушна. И это делает её по-настоящему опасной', 'Вернор Виндж'),
('Невозможно бороться с природой — ты либо живёшь с ней, либо умираешь', 'Дик Проенеке'),
('Где кончается комфорт, начинается настоящая жизнь.', 'Беар Гриллс'),
('В дикой местности побеждает не тот, кто силён, а тот, кто умеет ждать.', 'Джо Симпсон'),
('Природа не зла и не добра — она просто есть. И человек либо принимает её правила, либо гибнет', 'Джон Кракауэр'),
('В сердце каждого человека спит зверь. Природа лишь будит его', 'Джек Лондон'),
('Дикие просторы не прощают ошибок. Здесь каждая мелочь — вопрос жизни и смерти', 'Джеймс Оливер Кервуд');
INSERT INTO paragraphs (paragraph_number, paragraph_descr, effect_id) VALUES
(1, 'В сгустившемся мраке вы видите что-то невероятно прекрасное, что заставляет вас упасть на землю без сил и расплакаться: судорогами, без слез. Среди деревьев виднеется слабо танцующий огонек. Кое-как вы добредаете до крохотной избушки, в которой вас встречает лесник: бледный и недоверчиво вас изучающий. Немудрено, что он принял вас за призраков. Он досыта вас кормит и рассказывает множество историй. Вы огромной, наконец-то сухой грудой тел валитесь на узкие нары — и засыпаете, понимая, что в этот раз природе не удалось взять над человеческой волей верх.', NULL),
(2, 'Кажется, здесь был крысиный яд.', 33),
(3, 'В воздухе стоит тонкая взвесь из кристаллов льда и снега. Она не “идет”, просто сыпется в глаза и за воротник; будто б зависла в воздухе и реагирует лишь на ваше приближение. Яркий солнечный свет заливает ледяную пустошь, и через какое-то время вам начинает казаться, что глаза вам выжигают паяльной лампой.', 117),
(4, 'С высоты склона зимний рассвет особенно красив. Лед и снег впитывают нежно-малиновый свет, и пейзаж становится сюрреалистичным, похожим на мертвую поверхность Марса. Когда вы просыпаетесь, не чувствуя онемевших конечностей, эта красота заботит вас меньше всего. Стуча зубами, вы с трудом собираете нехитрые пожитки и продолжаете безрадостное путешествие. Спустя несколько часов вы упираетесь в непроходимый лесной массив. К счастью, вбок от него вниз бежит заросшая тропка. Вы без особого труда спускаетесь обратно к берегу. Повсюду лежат завалы выбеленного плавника, похожего на кости динозавров. Вы пополняете запасы древесины и разжигаете большой, жаркий костер, но не успеваете отогреть конечности, как неподалеку раздается звук.', 34),
(5, 'Вечером вами овладевает мрачная решительность. Вы смотрите в огонь и думаете о том, что ждет там, в мире людей и машин. Вам кажется, что может, и не хотели бы туда возвращаться, но выживание, преодоление зажигает в вас давно забытый огонь. Вы ложитесь спать с твердым намерением отдохнуть.', 35),
(6, 'Вы чувствуете, как учащается сердцебиение, руки наливаются силой. Однако теперь ваша кровь хуже сворачивается.', 1),
(7, 'Вы бродите по мшистому болоту кругами, теряя остатки драгоценной энергии. Голоса, будто издеваясь, становятся то ближе, то дальше, вне зависимости от выбранного направления. В конце концов вам остается лишь махнуть рукой и устроиться на мокрый, неуютный ночлег.', 36),
(8, 'В шуме дождя вы слышите странные голоса. Что это, подмога, слуховые галлюцинации, или интуиция, подсказывающая верный путь?', NULL),
(9, 'На заросшей колее, когда-то бывшей автомобильной дорогой, вам повезло найти дорожную аптечку, по всей видимости однажды вывалившуюся из чьего-то салона. Каркас у нее жестяной, и внутри обнаруживаются просроченные лекарства, бинты и йод. Благодаря удачной находке вам удается немного подлататься.', 2),
(10, 'Дождь смыл кого-то из ваших товарищей вниз по бесконечным рекам. Найдут ли их тела, или они навсегда останутся в холодных северных водах? Поверят ли вам, если вы расскажете хоть кому-то, как было на самом деле? Вы не знаете. Помощь вы нашли случайно: наткнулись на лагерь сплавщиков, застрявших у реки по милости дождя. Провианта у них навалом, есть и запасная палатка. Скоро вы вернетесь домой, но как вы будете смотреть в глаза родственникам тех, кого потеряли по пути?', NULL),
(11, 'Яркое солнце, постоянное напряжение от ледохода и нервное истощение наконец начинают брать над вами верх. Вперед вы ползете как мухи, почти проваливаясь в смертельные расселины между льдами. Вечером закат заливает море не красным, но недобрым зеленоватым светом. Признаться, такое вы видите впервые. Стоит солнцу поравняться с горизонтом, как у кромки льдов вы видите небольшую лодку, - не моторную, но обтянутую шкурами, - совершенно черную. Надежда на спасение одолевает инстинкт самосохранения, и вы приближаетесь к судну. С его борта, казавшегося безлюдным, спрыгивает несколько смутных теней с примитивными гарпунами…', 37),
(12, 'Земля настолько размокла от ливней, что проседает пластами, то и дело заставляя вас барахтаться в мутной жиже. Когда вы выбираетесь к кромке леса, обнаруживаете, что на многих деревьях растут размокшие от дождей грибы-трутовики. Вы сомневаетесь в их съедобности, но голод — не тетка, и вы собираете их про запас.', 3),
(13, 'Вы успешно добиваете зверя и извлекаете его тело из прожорливой жижи.', 4),
(14, 'Немногим позже вы набредаете на тушу лося. Неизвестно, сколько он пролежал здесь, в полузатопленной низине. На туше есть следы зубов, так что это — не совсем падаль, но все же есть его мясо может быть опасно.', 118),
(15, 'Когда вы видите на горизонте огни города, в вас просыпается странное чувство. Вы сотрясаетесь в рыданиях, скрипите гнилыми пеньками измученных цингой зубов... Вы оборачиваетесь и воочию видите скрюченные тела своих товарищей. Все они мертвы — как знать, может, не без вашей вины? Вы делаете последний шаг к золотым, переливающимся на морозе огням города и падаете на колени. Вам нельзя к людям. Вы перешли черту. С животным воем вы втыкаете голову в подтаявшую шугу и затихаете — навеки.', NULL),
(16, 'Боль отступает, но сознание притупляется. Вы чувствуете, что руки и ноги не сразу подчиняются приказам.', 5),
(17, 'Вода просачивается сквозь разбитые ботинки. Еще немного — и обморожение гарантировано всем вам. Нужно что-то делать, но хватит ли у вас на это сил? Вы решаете...', 119),
(18, 'Бедолага в очередной раз приходит с пустыми руками с охоты, и ваши фрустрация и усталость находят выход в вспышке кровавой ярости. Откуда только у вас взялись силы? Вы налетаете на несчастного скопищем обезумевших кричащих оборванцев. Несколько мгновений спустя вы приходите в себя у окровавленного, скрюченного тела. Стараясь не смотреть друг другу в глаза, вы продолжаете безрадостное путешествие. Остатки сил и крохи рассудка теперь пожирает невероятное отвращение к самим себе.', 38),
(19, 'Вы окончательно истощены, ваши физические и духовные силы почти иссякли. Вы с трудом пробираетесь сквозь огромное замшелое болото, то и дело проваливаясь в забытье. Вдруг один из вас истошно кричит. Кочки под ногами начинают бугриться, вспучиваться, и прямо перед вами вырастает огромное, постоянно меняющееся нечто. Начинается сумасшедший, бессмысленный бой…', 39),
(20, 'Вскоре ты поднимаешься по каменистому отвалу: коричневому и сыпучему. Перекатившись на спину, ты встаешь и тупо рассматриваешь километровый столбик. Через какое-то время тебя начинает разбирать дикий смех, ты падешь на колени  и долго  плачешь мучительными, бесслезными спазмами. Ты плачешь до тех пор, пока рядом не останавливается автомобиль, которого ты не замечаешь. Из ступора тебя выводит вопрос, который с давних времен стал визитной карточкой этой бесконечной северной дороги: — Вам помочь?', NULL),
(21, 'Дни так похожи один на другой, что каверзная мысль о том, что вы ходите кругами, все время рядом. Она грызет вас где-то в области бессознательного. Ни усталость, ни скотское безразличие, которое все сильнее вас охватывает, не мешают ей подтачивать ваш разум.', 40),
(22, 'Вы стоите у какого-то подобия здания — впервые за столько дней вы чувствуете на лице бетонное дыхание цивилизации. Далеко за ним виднеются такие же серые коробки без окон. Вы не помните их на карте, да и странно видеть такие новостройки здесь, где балом правят полуразвалившиеся бараки из лиственниц. Вы скептически оглядываете подобие поселка перед собой. Стоит ли тратить на него время?', 120),
(23, 'Ваша группа продолжает путь. Нужно поскорее вырваться из этой лесной клетки, нельзя терять ни минуты.', 41),
(24, 'Тратить и без того скудный запас ресурсов вам решительно не хочется, и вы выбираете самый примитивный и опасный способ преодоления.', 42),
(25, 'Ледяные шипы вряд ли превратятся во что-то совсем уж острое и непроходимое… Так вы подумали. На самом же деле, когда вы выбрались на ровный лед, ступни у вас кровоточили, примерзая к соленой ледяной поверхности. Это причиняет такую адскую боль, что вы не в состоянии мыслить разумно, и просто идете вперед, пока не падаете от изнеможения.', 43),
(26, 'Очередной день пролетает незаметно. Однообразный пейзаж, медленно проплывающий мимо, мучает вас: кажется, будто вы топчитесь на месте. Когда эта чудовищная пытка закончится? Вы не знаете, и знать не можете. Короткая ночь заканчивается, и вы отправляетесь в путь.', 44),
(27, 'Лес постепенно стал вашим лучшим другом. Вы знаете все его секреты. Знаете, куда лучше не соваться. Где происходит что-то, что не под силу понять городским жителям. Лес стал вашей второй душой. Вы не можете найти ни одной причины возвращаться в город. Зато здесь, в лесу, вы стали чем-то большим. Однажды вы наткнулись на небольшой лагерь, где у костра грелись какие-то люди. Один рассказывал другому, что в этих краях завелся леший. Второй не верил, громко хохотал и матерился. Вы пожали плечами, развернули тропу, по которой они пришли, вспять, и молча растворились в прохладном воздухе.', NULL),
(28, 'Кто-то из вас, похоже, решил взять ситуацию в свои руки! Короткая потасовка, сдавленный хрип... Кровь лениво сочится из ран: кажется, ваши тела не способны более создавать необходимое для этого напряжение. Зато на создание напряжения все еще способна животная, обесчеловеченная психика! Как бы то ни было, в отряде стало на один голодный рот меньше.', 45),
(29, 'Вы снова слышите эти голоса. Они куда-то зовут вас, и вы уже не в силах им сопротивляться. Вы слишком вымотаны, чтобы рассуждать разумно. Голоса заводят вас в мшистое болото. Силы окончательно покидают вас, и вы, погрузившись в забытие, ложитесь между кочками, надеясь хоть немного перевести дух. Но больше вы не поднимаетесь. Теперь вы - часть болота, а болото - часть вас.', NULL),
(30, 'Вы переводите дыхание и оглядываете поле боя. Сердце колотится как бешеное. Внезапно приходит осознание того, что одежда мокра насквозь, а вместе с ним — безумная надежда на то, что мокра она от пота, а не крови. Когда вы собираетесь с силами и продолжаете свой путь, лес освещается лишь огромной, круглой луной на небе. Вас всегда удивляло, насколько незначителен ее свет в городе, среди фонарей, и как отчетливо, хоть и жутковато, она обрисовывает деревья, камни и тропы..', 46),
(31, 'Вы погружаетесь в полубезумное состояние. В голове каждого из вас пульсирует лишь одна задача: выжить любой ценой. Мечта о том, что вернуться домой удастся всем, кажется все более и более утопической... Пора признать очевидное: вы начинаете доходить. Вы не можете смотреть друг на друга не только без боли, но теперь — и без плотоядного интереса. Каждый кусок, поглощаемый товарищем, вызывает у вас острую ненависть. Так продолжаться не может. Вы решаете...', 121),
(32, 'Очередной день пролетает незаметно. Однообразный пейзаж, медленно проплывающий мимо, мучает вас: кажется, будто вы топчетесь на месте. Когда эта чудовищная пытка закончится? Вы не знаете, и знать не можете. Короткая ночь заканчивается…', 47),
(33, 'А стоит ли оно того? Вы принимаете решение не тратить силы на этот богом забытый ГАЗ. В любом случае, миновав овраг, ваша группа продолжает путь.', NULL),
(34, 'Вы пытаетесь как можно плотнее закутаться в одежду и спальники, но свет будто бы проникает сквозь ткань и веки, от него не скрыться... Заснуть вам так и не удается, и вы встречаете первые лучи зари опустошенные и злые. Стоит первым проблескам солнца озарить ледяную пустыню, как свет пропадает, будто б его и не было. Вы собираете пожитки и отправляетесь в путь.', 48),
(35, '«Если тропа — медвежья, то нам каюк», — вы опасливо посматриваете на колею. — «Медведица с пестунами — то, чего нам как раз не хватало». Вы проходите оставшееся расстояние до бурелома и смело вламываетесь в самую его гущу. Через пару часов вы серьезно раскаиваетесь в своем решении. Дикий стланик местами разросся настолько, что толстые, неподатливые ветви в их крепкой сцепке друг с другом раздвинуть просто невозможно. Вы тратите много времени на обход таких зарослей, а их становится все больше и больше. Наконец вам удается найти высоченный куст и взбираетесь по ветке-стволу повыше. Осматриваетесь, с отчаяньем видя лишь зеленое море в обе стороны. Затем, будто в детстве, начинаете прыгать с ветки на ветку. Внимание притупляется, и внезапно нога одного из вас оказывается в цепких, скрюченных пальцах кустарника. Стопа его подворачивается, и он летит вниз. Ногу обжигает огнем, и бедняга шипит сквозь зубы. Место вывиха моментально распухает.', 49),
(36, 'Ближе к вечеру вы разом останавливаетесь, не веря глазам. Надо льдом ближе к морской кромке полыхает зеленое зарево. Его можно было бы перепутать с северным сиянием, если бы не отсутствие характерного треска... И чего-то еще. Зеленые всполохи методичны, будто вспышки света с маяка или корабля. Таинственный свет не дает сомкнуть глаз, хоть вы и чертовски устали. Вы решаете...', 122),
(37, 'Кое-кто из вас стал приносить слишком мало пользы на благо выживание группы. В условиях постоянной нехватки еды и ресурсов хлипкие нервы остальных участников начинают сдавать.', 123),
(38, 'Вы чувствуете, будто ноги сами идут под чеканный мотив. Вы чувствуете себя чуть бодрее.', 6),
(39, 'Вы движетесь с черепашьей скоростью. Те, кто повыше, то и дело обрушивают на ползущих внизу соратников потоки песка и каменной крошки. Вечер застает вас уже на вершине: расцарапанных, обессилевших и голодных. Кое-как вы расчищаете место для ночлега и впервые за долгое время умудряетесь набрать какого-никакого хвороста для костра. Однако впереди стеной стоит лес, а снег здесь глубокий... Здесь, на вершине, дует промозглый ветер, холод высасывает последние силы.', 50),
(40, 'Плесень! Все ваши запасы еды поражены отвратительной, скользкой плесенью. Вы с сожалением вынуждены выбрасывать все больше и больше еды. Должен быть какой-то способ от нее избавиться...', 51),
(41, '«Звери — не дураки», — думаете вы. — «В буреломе вряд ли нас ждет что-то хорошее». Звериная тропа на самом деле мало отличалась от вашего предыдущего маршрута. Через какое-то время и она упирается в бурелом. Вы какое-то время идете вдоль плотной стены векового, разросшегося стланика, кляня на чем свет сделанный выбор. Вдруг вы обнаруживаете приличных размеров лаз над смыкающимися стволами кустарника. Без особых раздумий ваша группа решает заползти внутрь. Земля прохладная и влажная на ощупь, да и в целом под хвойным куполом температура оказывается существенно ниже. Вы ползете уже достаточно долго, когда впереди отчетливо слышится какое-то шебуршание. Сердце уходит в пятки, но пути назад нет. Вы упрямо ползете к виднеющемуся впереди просвету и мужественно встречаете опасность лицом к лицу!', 52),
(42, 'Море еще не вскрылось, но воздух просолен. Соль оседает на одежде, лице, волосах. Все это дубеет, покрывается коркой. Из-за соли и морозов кожа ваша растрескалась, и боль с зудом давно уже стали вашими верными спутниками.', 53),
(43, 'Лёд вспучивается и вздымается на глазах. Или это лишь кажется вам под увеличительным стеклом воспаленного снежной лихорадкой воображения? Стало ощутимо теплее, и вас уже не мучает беспощадный холод. Только вот лед перестал быть надежной опорой. Он набухает синим, становится вязким, все больше похожим на смертельно опасную шугу. Стоит вам промочить ноги — и на вашей жалкой экспедиции можно будет поставить крест. Нужно избавиться от части припасов, чтобы не увязнуть в снегу.', 54),
(44, 'Вы понимаете, что выжили не просто так. За каждого из вас, кто смог добраться до спасительной цивилизации, принесли жертву, заплатили цену в человеческую жизнь ваши покойные товарищи. Вы ликуете, но и понимаете, что теперь вам придется жить как-то иначе. Ведь за спиной у вас остались те, кто не дошли. И, похоже, вам придется жить и за них тоже.', NULL),
(45, 'Вы незамедлительно засовываете руку по локоть, как вдруг, вас что-то кусает! Однако в кровоточащем кулаке он держит горсть кедровых орехов. К сожалению, рана мгновенно опухает, и в сырости скоро начинает гноиться. Что ж, по крайней мере, у вас будет, чем перекусить.', 55),
(46, 'Вскоре вы натыкаетесь на редкое для этих мест разлапистое дерево, под кроной которого находите какое-никакое укрытие. Дождь зарядил пуще прежнего, и от нечего делать вы изучаете его могучий ствол... И находите в нем небольшое, как раз руку просунуть дупло. Осталось понять, как найти ему применение…', 124),
(47, 'Вы уходите прочь, тоскливо оглядываясь на манящие ягодки. Впрочем, возможно, ваша группа избежали куда большего зла, чем пустой желудок.', NULL),
(48, '«Черт с ним, рискну», — решаете вы и, аккуратно переступая, направляетесь к источнику света. Вы с черепашьей скоростью ползете к источнику света, нащупывая подошвами пружинящие кочки. Глубина становится все больше, и скоро вы оказываетесь по грудь в зловонном озерце. Вода стоялая, и пахнет от нее смертью. Источник света предательски пляшет между деревьями, будто фонарик, или… Вы взываете к последним крохам рациональности, но темный лес и идущая на убыль Луна над болотом возвращают ваше сознание почти в первобытное состояние. «Э-ге-гей!» — кричит один из вас. Ответа не слышно, зато из кустарника неподалеку шумно вспархивает стайка куропаток. От неожиданности кто-то теряет равновесие и падает навзничь.', 56),
(49, 'Через несколько часов вы находите широкую поляну, покрытую черной ягодой. Не в силах устоять перед искушением, вы наедаетесь, насколько это возможно ее водянистой, безвкусной мякотью и становитесь лагерем неподалеку. Вскоре всех начинают мучить жуткие колики, и вы ложитесь спать, не в состоянии заниматься чем-то полезным.', 57),
(50, 'Если вы хотите вернуться домой живыми, вам необходим хотя бы короткий привал. Спустя некоторое время вы уже валяетесь без сил, прижавшись друг к другу у небольшого костра. Кто-то достает из рюкзака замерзшую корюшку и решает приготовить на костре. Запах жаренной рыбы будит остальных, и несчастный кусочек провизии приходится разделить на всех. О последствиях безобидной кулинарии вы не задумываетесь.', 58),
(51, 'Вы слышите приглушенный рев, едва различаемый в шуме дождя. Его источник оказывается неподалеку: молодой медведь, попавший лапой в незнамо кем и когда установленный тут капкан. Он очень ослаб, но все еще способен дать бой… Прямо у его лап вы замечаете ружье.', 125),
(52, 'Вы долго спорите, стоит ли игра свеч. Кто знает чем мог болеть тот зверь. Вдруг, вы слышите какой-то звук...', 126),
(53, 'Вы поняли, что не зря решили залезть в ледяную воду. Да, может быть ваша одежда промокла, но это того стоило.', 59),
(54, 'Тратить время, силы и ресурсы на случайно забытую кем-то железку посреди ледяной пустыни кажется вам безумием. Вы продолжаете свой безрадостный путь и вовремя успеваете найти покрытый густой порослью «карман». От осознания того, что из-за этого день можно считать удачным, веселее вам не становится.', 7),
(55, 'Вы выходите прямо к городской бухте. Отчаянные рыбаки, удящие в раскисших прорубях рядом со стокилограммовыми машинами, смотрят на вас безумными глазами. Кого-то вы несете на себе, кто-то еле-еле переступает сам. Вас охватывает спокойная, теплая радость. Вы выжили, но не в привычном понимании этого слова. Вы смогли выжить еще и внутри, сохранить что-то очень важное и человечное. Вы ни разу никого не положили под нож, хоть у каждого и были подобные мысли. Победа не в том, что вы добрались до цивилизации, а в том, что смогли задавить зверя в себе.', NULL),
(56, 'Дух красного льда Вы готовы поклясться, что вдали виднеются огни города. Из груди у вас вырывается сдавленный вой. Вы решаете преодолеть последний отрезок пути без ночевки. Тем более, что ночь обещает быть лунная. Луна действительно восходит, ярко освещая все расселины, все изгибы льда. Только цветом она кроваво-красная, и лед начинает подозрительно напоминать раскуроченные шматы плоти… Стихает ветер, шум воды. Кажется, будто даже льдины крепко смерзлись друг с другом, они больше не прыгают у вас под ногами. Или даже не смерзлись, а… срослись? Вы гоните ненужные мысли прочь: огни все ближе. Аккурат между вами и городом возникает небольшой черный комок. Инстинктивно вы пытаетесь его обойти, но, как бы вы ни поворачивались, стоит ему ненадолго пропасть, как он показывается вновь прямо по курсу и ближе. В конце концов нечто прыгает вам прямо под ноги. Оно не черное, а красное, и будто выточено из льда. И каким-то образом напоминает всех животных одновременно…', 60),
(57, 'Вы собираетесь в кучку, словно пингвины, и вяжетесь веревкой. Теперь вы идете короткой цепочкой, не рискуя потеряться в снежном шторме. Ночь выдается ужасная, но, по крайней мере, все живы.', 61),
(58, 'Лишних ресурсов у вас нет, и слепнуть тоже не хочется. Вы уходите ближе к берегу, в холодную, но милосердную тень и мучаетесь от безделья до вечера. Впрочем, вы успеваете отдохнуть, но за день вы совсем ничего не прошли.', 62),
(59, 'Перед вами встает непростой вопрос: погружать в полынью железку, или веревку, а за железку держать? Вы решаете утопить веревку, а железку придавить вещмешком. Через пару часов вы решаете проверить “удочку” и обнаруживаете, что железка благополучно выскользнула и покоится теперь на дне моря. Чего и следовало ожидать.', 63),
(60, 'Вы решаете, что узкая выемка в скалах надежно защитит вас от ветра и непогоды. Вы уютно устраиваетесь между камней, разжигаете костер, кое-как ужинаете и ложитесь спать. Просыпаетесь вы внезапно, от чудовищного ощущения чего-то мокрого по всему телу, будто бы вас окатили ведром ледяной воды. Оказалось, что огонь растопил нависший над вами снежный козырек, и теперь вам срочно нужно сушиться. С огромным трудом вы вновь разводите костер; о спокойном сне вам теперь нечего и мечтать.', 64),
(61, 'Под конец дня, когда лед в очередной раз покраснел от света заходящего солнца, небо неожиданно почернело. Сначала с неба начал сыпать мелкий, похожий на порох снежок, потом поднялся ветер - и вот уже над ледяным полем начался настоящий буран. Снег сечет вам лицо, и вы с трудом видите друг друга в метели…', 65),
(62, 'Бой воображаемых барабанов становится все сильнее. Вы чувствуете прилив сил.', 8),
(63, 'Человек человеку — волк, а когда дело касается золота — тем более. Съезжать на обочину с трассы можно по разным причинам, но бросаться в объятия бездне вы желанием не горите. Вы вспоминаете все, через что прошли. Тут, в глуши, ваши жизни все еще в опасности, и кто знает, зачем здесь эти люди? Бандиты есть всегда и везде… С тяжелым сердцем вы возвращаетесь на тропу.', NULL),
(64, 'Какое-то время спустя вы набредаете на очень странное дерево. Оно стоит как бы поодаль остального леса, и все состоит из белого, высохшего ствола и странных отверстий. Заходящее солнце оказывается на одной линии с деревом, и вы можете поклясться, что алые лучи, прошедшие через полости, на миг превратились в распахнутые глаза… Как бы то ни было, ваше зрение обострилось, и вы видите очертание каждого камушка в наступающей темноте.', 9),
(65, 'Какое-то время спустя вы набредаете на очень странное дерево. Оно стоит как бы поодаль остального леса, и все состоит из белого, высохшего ствола и странных отверстий. Заходящее солнце оказывается на одной линии с деревом, и вы можете поклясться, что алые лучи, прошедшие через полости, на миг превратились в распахнутые глаза… Как бы то ни было, ваше зрение обострилось, и вы видите очертание каждого камушка в наступающей темноте. В следующий бой все игроки получают +2 к атаке.', NULL),
(66, 'В ваши головы закрадываются мысли о том, что это путешествие может оказаться последним. Вы поднимаете голову к небу и закрываете глаза, остановившись. Тишина такая, что звенит в ушах. От нее одновременно и спокойно и страшно. Вам хочется…', 127),
(67, 'Вам и так хватает неприятностей, думаете вы, и обходите несчастного медведя стороной.', NULL),
(68, 'Спустя какое-то время вы выходите к самой кромке прижима. Мимо медленно плывут крупные льдины. В поле вашего зрения появляется средних размеров льдина, стремительно скользящая по водной глади. На ней вы замечаете что-то железное… Рассмотреть нет времени, надо принимать решение!', 66),
(69, 'Что это: извращенное подобие демократии, или возвращение к кровавым корням общественно-племенного строя? Вы проводите быстрое, стыдливое голосование. Несчастный больше не получает из общака ни куска. Какое-то время он еще плетется за вами, но потом замирает безжизненной кучкой тряпья посреди ледяной пустыни. Как бы то ни было, в отряде стало на один голодный рот меньше.', 67),
(70, 'Солнце стремительно катится по небосклону. Вы готовы поклясться, что движение теней, отмечающее его заход, видно невооруженным глазом. Впрочем, глазам вы уже привыкли не верить: сияющий лед, недосып и голодание сделали тех ненадежными союзниками. Поднять голову ни у кого из вас нет сил, так что вы лишь бредете прямо, надеясь не поскользнуться.', NULL),
(71, 'Ни у кого из вас не возникает сомнений, что это наиболее надежный вариант. Течение очень сильное, поэтому на возведение переправы уходит много ресурсов и времени. Но игра стоит свеч, и вы перебираетесь на другой берег целыми и невредимыми.', 68),
(72, 'Вы приходите в себя медленно, по одному. По "пробуждении" у каждого из вас в голове вспыхивает жуткая и одновременно легкая мысль, что он остался один, и товарищи то ли его бросили, то ли успели сгинуть где-то во льдах. Впрочем, наваждение рассеивается почти мгновенно, и вы медленно сползаетесь друг к другу: крошечные черные точки на бескрайнем сером льду. Какое-то время вы не можете даже понять, в какую сторону шли. С огромным трудом вы находите едва различимые следы подошв на просоленном льду. За жалкий остаток дня вы едва проходите несколько сот метров, и вновь погружаетесь в бесноватое забытье. Ночью вас будит адский треск, будто бы предвещающий Апокалипсис. Кажется, лёд начинает идти.', 69),
(73, 'Отлично! Вам удалось смастерить традиционную приманку для любопытных нерп. Ждать пришлось долго, но в конце концов небольшая нерпочка высунула голову, привлеченная странным звоном. Вы моментально ее гарпуните и получаете гору провианта!', 10),
(74, 'Вы решаете пойти по вешкам. Ну что ж, вам повезло! Кто-то из вашей группы натыкается на чей-то схрон с отсыревшими патронами и просроченными консервами.', 11),
(75, 'Цивилизация обещает вам спасение, но в лесу она зачастую - незваная гостья. Вы чувствуете, что до Трассы остается совсем немного: об этом говорят следы пребывания здесь человека. Одним из таких следов становится старый капкан, в который одного из вас угораздило угодить. К счастью, пружина потеряла убийственную мощь, и бедолаге вскоре удается выбраться из ловушки.', 70),
(76, 'Пройдя немного вперед, вы замираете в изумлении. В просвете между деревьями мерцают таинственные огни, отражающиеся в водной глади. Вы видите их с долю секунды, а потом они пропадают, будто бы их никогда и не было. Или то были просто пятна перед глазами измученных долгой дорогой людей? Вы не находите ответа на этот вопрос, зато находите путь к твердой земле.', 71),
(77, '«Черт с ними», — думаете вы, отворачиваясь. — «Не хватало еще наткнуться на звериное лежбище. Трасса недалеко» Миновав поселок, вы быстро забредаете в болото. Ноги быстро намокли, вас окружил вездесущий гнус. Вы хлюпаете по жиже до самого вечера и вынуждены остаться на ночлег на разросшейся, покатой кочке. Всю ночь вы спите урывками, то и дело сваливаясь с ненадежного насеста. Как только светает, вы с трудом поднимаетесь на затекшие ноги и продолжаете путь.', 72),
(78, 'Это точно галлюцинации, думаете вы и, стараясь не обращать внимание на голоса, ваша группа ложится отдыхать.', 12),
(79, 'Проходя мимо очередного кармана, лидер группы оглядывается и замечает, как сильно растянулась ваша полуживая вереница.', 128),
(80, 'Вас касаются руки в крепких, теплых перчатках. Ощущение тепла, невесомости, полета — совсем как в детстве. Одно вы знаете точно: эта проклятая экспедиция навсегда искалечила вас внутри и снаружи.', NULL),
(81, 'Вы не знаете, когда на вашем пути снова встретится источник питьевой воды, и, конечно, не упускаете возможность пополнить запасы.', 13),
(82, 'Вы — не звери, и в жертву приносить никого не собираетесь. Потерявших сознание вы взваливаете на плечи и с угрюмой решимостью продолжаете свой бесконечный поход... К сожалению, на одной морали вы продержитесь не долго.', 73),
(83, 'На одном из деревьев вы замечаете растрепанную тряпочку-вешку, с которой, как и с вас, уныло капает вода. Вдали виднеется еще одна, но вешки ведут куда-то в непролазную чащу.', 129),
(84, 'Вы набираете полную грудь воздуха и кричите что есть сил. Умолкнув, вы чувствуете себя до странного опустошенным.', 14),
(85, 'Красное. Повсюду красное. В мозгу — черви. Что это? Золотые огни. Обжигают глаза. Спрятаться в лес... Уйти во льды... Безопасно, тепло. Нужно лишь найти больше красного…', NULL),
(86, 'Идти по темному лесу страшновато: ваш праздный мозг вновь и вновь перебирает истории о путниках, рискнувших бросить вызов родному краю и оставшихся где-то между бескрайними сопками. Пока вы идете, в голове сама собой возникает какая-то дикая, примитивная мелодия, соразмерная с вашими шагами. Где-то на подкорке вы начинаете волноваться: сколько километров вы уже прошли? Хватит ли сил, сможете ли продержаться на скудном рационе?', 130),
(87, 'Вам осточертело таскать за собой полумертвого соратника. Вы просто опускаете его на лед, а тот будто б и не замечает. Спиной вы чувствуете: стоило вам отойти на несколько шагов, как тот испустил дух... Почувствовал ли он, что его все покинули, или стоило пронести его еще несколько метров, чтобы вашу совесть не грызли до самой смерти когтистые демоны?', 74),
(88, 'Ржавчина! Ваши ресурсы покрываются не предвещающей ничего хорошего коричневой пленкой. Веревки гниют и рвутся, железо становится хрупким и крошится. Нужно искать решение: возможно, вас спасет животный жир...', 75),
(89, 'Вы решаете перегнать шторм. Спустя несколько часов, вы понимаете, что шторм перегнать невозможно. Когда вы уже потеряли надежду выжить, вы чудом натыкаетесь на крохотную расселину в скалах. Вы забиваетесь в нее полным составом, демонстрируя чудеса ловкости. По крайней мере, остаток ночи вы проведете в относительном тепле. Впрочем, наутро вы обнаруживаете свою поклажу, которую пришлось бросить снаружи, растасканной ветром по всему ледяному морю. Вам хватает фантазии представить на месте своего нехитрого скарба собственные окоченевшие трупы, так что в какой-то степени вы благодарны судьбе.', 76),
(90, 'Приход вечера для вас — благословение. Вы привычно зажигаете костер, но сон не идет: уставшие тела отказываются отдыхать. Стоит закрыть глаза, как вам начинают мерещиться шаги и шорох в кустах. Вы решаете…', 131),
(91, 'Разум окончательно вас покинул. Один из вас стал пищей для остальных. Чудовищно... Как бы то ни было, в отряде стало на один голодный рот меньше.', 77),
(92, 'Усталость и голод одолевают вас как никогда прежде. Вы преодолеваете очередную распутицу, кляня вечный дождь и слякоть на чем свет стоит. В конце концов вы выбираетесь на опушку, покрытую странной ярко-оранжевой ягодой. Кажется, будто в сгущающейся темноте та слабо люминесцирует... Выглядит она аппетитно, но вы сомневаетесь...', 132),
(93, 'Вы слышите вдалеке человеческие голоса. У вас от радости перехватывает горло, вы устремляетесь к ним напролом — через кусты и деревья. Однако через пару минут вы замедляете шаг. Собеседники кричат друг на друга, и голоса их становятся все громче. Стоит ли рисковать с незнакомцами в лесу, когда спасение так близко?', 133),
(94, 'Лед припорошен снежком, но от того не менее коварен. Каждый из вас уже пару раз падал, и сполна ощутил на себе его неровности и бугры. По вечерам он загорается ярко-малиновым светом, но это прекрасное зрелище нисколько вас не трогает. За то время, что вы ползете по прижиму, вы научились его ненавидеть. Вдруг кто-то из вас вновь подскальзывается и падает, сильно ударяясь коленом обо что-то во льду. Немного повозившись, вы достаете это и гордо демонстрируете находку товарищам.', 15),
(95, 'Из последних сил вы несете соратника на себе, хоть тот и имеет все шансы никогда не прийти в себя. Но его жизнь — в ваших руках, и на его месте вполне могли бы быть вы...', 78),
(96, 'Вы идете назад, но не находите ни одной развилки. Похоже, вы потратили день пути зря.', 79),
(97, 'Несколько часов спустя непролазный бурелом по правую руку вытесняет вас под склоны опасного на вид оврага. Какое-то время вы крадетесь под угрожающе нависшими земляными сводами, нутром чуя неладное. И вот до вас доносится грозный рокот: оползень! Стремглав вы мчитесь прочь от несущейся в вашу сторону массы камней и грязи, то и дело оступаясь и поскальзываясь. В конце концов вам удается укрыться среди деревьев, но без потерь не обошлось...', 80),
(98, 'Каждое лишнее мгновение, проведенное в ледяном плену, кажется вам невообразимой пыткой. Вы настолько отупели, что неспособны мыслить на шаг вперед, и можете лишь продолжать бездумно переступать мокрыми ногами по издевательски чавкающей шуге.', 81),
(99, 'Такие шутки природы - не редкость, и вы пытаетесь не обращать на них внимания. Ближе к утру вам удалось забыться коротким, беспокойным сном, который немного восстановил ваши силы.', 16),
(100, 'Спустя какое-то время вы становитесь свидетелями странного зрелища: на ближайшей сопки едва различимая человеческая фигурка совершает неестественные, но довольно простые пируэты. Через несколько минут фигурка исчезает, и вы готовы поклясться, что облака над сопкой рассеялись, сквозь прорехи даже проглядывает солнечный свет.', 134),
(101, 'Один из вас благородно решает принести себя в жертву ради других. Или он просто-напросто сдался? Он просит лишь оставить заточенный кусочек металла, на случай, если смерть от обморожения окажется слишком мучительной. Вы молча оставляете его за спиной, одновременно радуясь, что не разделили его участь, и завидуя концу его мучений. Как бы то ни было, в отряде стало на один голодный рот меньше.', 82),
(102, 'Спасать приходится самого охотника.', 83),
(103, 'Часть вашего существа боится этой мелодии, хоть вы и не можете понять, почему. В конце концов страх пересиливает, и вы легонько бьете себя по лбу, чтобы отогнать потустороннюю музыку. Вам это удается.', NULL),
(104, 'Вы с умным видом опускаете привязанный к веревке камень в полынью и ждете пару часов, не “клюнет” ли. В конце концов перемерзшая веревка лопается, и “приманка” уходит на дно… правильно, камнем. Как бы то ни было, вам остается лишь продолжать путь.', 84),
(105, 'Лидер призывает группу не останавливаться, но самый любопытный из вас, немного отстав, решает все же засунуть руку в дупло. Раздаётся крик. Вы оборачиваетесь и видите, что рука бедолаги кровоточит.', 85),
(106, 'Вы с огромным трудом подожгли какие-то жалкие оставшиеся у вас ошметки, израсходовав весь газ. Какое-то время они дымят на льду, выплевывая крохотные огоньки пламени, а затем превращаются в унылую кучку углей и сажи. Стоит признать, что решение было далеким от гениального.', 86),
(107, 'Долгая дорога порядком вас измотала. Пока что вам удается следовать заброшенным дорогам, но дорога нет-нет, да упирается в заросли ольховника. Предательски низкорослые, заросли кустарника сильно вас задерживают, цепляясь за ноги, куртки, штаны. Вы решаете немного перевести дух, но вдруг…', 135),
(108, 'Пустотник Глубокой ночью вы выходите к огромному озеру, окруженному небольшой чахлой рощей. Кажется, будто бушующая непогода обходит это место стороной. Но, избавившись от проклятого дождя, вы не испытываете радости. Наоборот, затишье кажется вам тревожным. Озерная гладь непроглядно-черная. Она не шевелится: кажется, будто она сделана из матового черного стекла. Вы с ужасом наблюдаете, как эта гладкая поверхность вспучивается, из нее поднимается человекоподобная фигура и медленно приближается к вам…', 87),
(109, 'Вы приходите к выводу, что яркое солнце и снег - далеко не первостепенной важности проблемы, когда речь идет о вашем выживании. Вывод оказывается неверным, и вскоре все вы поголовно получаете ожог роговицы и практически слепнете.', 88),
(110, 'Идея потратить полдня на подъем по сомнительному склону кажется вам не очень привлекательной. Вы идете дальше, пытаясь казаться спокойными, но решение дается нелегко: переутомленный мозг вновь и вновь возвращается к мысли, что отказ от подъема, возможно, будет стоить вам жизни…', NULL),
(111, 'Кто-то из вас хватает раненого за шиворот и вы ужасе ныряете в овраг и затаиваетесь. В ушах звенит, а сердце колотится как бешеное. Вы выглядываете из укрытия - мужиков и след простыл. На поляне вы находите несколько рационов. Кто эти люди и почему они решили бежать — остается для вас загадкой. Недалеко вы находите разделанный труп оленихи, над которым уже вьются жирные синие мухи. Вы осматриваете следы в рассыпчатом грунте и замечаете, что важенка была не одна. Вторые следы намного меньше и ведут куда-то в заросли испуганной, запутанной цепью. Мотивы беглецов медленно проясняются в ваших головах.', 17),
(112, 'Вы решаете двигаться в сторону берега, надеясь, что один из “карманов” защитит вас от ветра. По пути один из вас отстает, и вы тратите драгоценное время, ища товарища впотьмах. Когда вам наконец удается укрыться, вы понимаете, что порядком отморозили себе лица и руки. Остается лишь надеяться, что гангрена не положит конец вашим злоключениям.', 89),
(113, 'Вы просыпаетесь от ощущения чего-то мокрого и холодного на лице. Не просто неприятного — его сводит мучительной судорогой, и вы изо всех сил пытаетесь вывернуться, толком еще не проснувшись. Это крыша палатки опять просела под напором дождя, и вам остается лишь выползти в промозглую ночь и скользкими, дрожащими руками попытаться починить хлипкую конструкцию. Попутно кляня себя на чем свет стоит, что ни вы, ни ваши спутники не взяли с собой тент. Каждый из вас надеется, что ливень вот-вот утихнет, хотя и это вряд ли вам поможет отыскать путь домой, ведь тропы размыло. Попав под шквал, вы окончательно заблудились.', NULL),
(115, 'Вы находите небольшой залитый водой овраг. На его дне ржавеет разбитый кем-то в незапамятные времена ГАЗ. Если хотите, можете попробовать добыть с него уцелевшие запчасти. Правда, придется изрядно извозиться...', 136),
(116, 'Вы решаете не поддаваться на провокации помутненного рассудка и продолжаете путь. Пока что без приключений.', NULL),
(117, 'Вы решаете, что для преодоления этой бурной реки вам достаточно перебросить веревку на другой берег и перебраться по ней. Вы связываете все обрывки, которые удалось найти в карманах. Один из вас подходит к самому краю, раскручивает веревку с крюком и  ловким броском цепляет его за ветку дерева на той стороне. Выглядит не слишком надежно, но вы рискуете.', 90),
(118, 'Вы набрели на остов сгоревшей когда-то заимки. Так часто происходит в наших краях: стоит себе избушка, и кто-то ее сжигает, или разбирает на топливо. Впрочем, иногда виновата печурка или неосторожность ею пользующихся. Провалившаяся крыша не послужит вам укрытием от дождя, но среди обугленных развалин вы находите несколько листов жести.', 18),
(119, 'Ближе к вечеру дождь превращается в самую настоящую грозу! Как назло она застает вас в открытой местности. Впереди виднеется скальный массив, под которым вы сможете без труда укрыться от непогоды и даже немного обсохнуть, но до него нужно еще добраться…', 91),
(120, 'Олениха вырывается из ловушки и скрывается в лесу', 92),
(121, 'Мир заливается красным цветом, ваши пальцы скрючиваются невероятной силы судорогой. В позвоночник будто вливают расплавленный свинец. Вас будто выворачивает наизнанку… И внезапно боль прекращается, вы начинаете видеть лучше, слышать запахи как никогда раньше. Что-то в вас навсегда поменялось, но вам никак не удается поймать мысль за хвост…', 19),
(122, 'Чуть позже вы натыкаетесь на своеобразную развилку и долго, тупо на нее смотрите. Старая дорога уходит куда-то в бурелом, от нее куда-то в сторону отходит еле видная колея примятой травы. Вы не помните этого маршрута, очевидно, усталость и дезориентация увели вас в сторону. Вы решаете...', 137),
(123, 'Песня получается грустной и тягучей. Вы замедляетесь, и внезапно обнаруживаете, что совсем остановились. Вам кажется, что вы плачете, но решаете не трогать лицо и просто продолжаете идти.', 93),
(124, 'Вы с трудом ковыляете дальше, но проходите совсем немного: останавливаетесь рано, чтобы, подобно раненому зверю, отлежаться перед долгой дорогой. На этот раз вы вдумчиво выбираете место для ночлега и ложитесь с твердым намерением проснуться бодрыми и отдохнувшими.', 20),
(125, 'Солнце плавно переползает вам за спину, и вы концентрируетесь на собственных тенях. Те идут вместе с вами, и в их присутствии вы находите странный комфорт. Засмотревшись, впереди идущий врезается прямо в трухлявое дерево. Он ругается: долго и страшно. Остаток дня вы идете медленно, чувствуя жалобное нытье в одеревеневших мышцах.', 94),
(126, 'Грибной царь В поисках укрытия вы забираетесь под массивную корягу, где надеетесь найти сухость и тепло. Однако под небольшим с виду корневым массивом вам открывается довольно просторная пещера. Вы благодарите судьбу и спускаетесь поглубже, чтобы развести костер… В центре пещеры растет огромный мясистый гриб грязно-зеленого цвета. Стоит вам приблизиться к этому чуду природы, как он выпускает в воздух целое облако удушливых коричневатых спор…', 95),
(127, 'Вы становитесь свидетелями жуткого зрелища: средних размеров олениха увязла в болоте. Добить ее не составит труда, но вот вытащить... Впрочем, она может обеспечить вас изрядным количеством провианта.', 138),
(128, 'Вы взбираетесь на сопку и падаете на колени от радости и неверия. Ваш невероятно обострившийся слух улавливает урчание мотора где-то далеко впереди. Остаток дня вы проходите почти марш-броском и ложитесь спать в полной уверенности, что наконец-то спасены.', 139),
(129, 'Один из вас спотыкается, падает и не может встать. Прежде чем вы успеваете осознать это событие, вы все успеваете уйти далеко вперед. Вместе с осознанием того, что надо вернуться, приходит озлобление. «Как он мог?» — стучит у вас в головах. «Он подвел нас! Пусть там и остается!» Впрочем, в вас еще сохранилось достаточно человечности, чтобы вернуться и помочь спутнику встать. Кажется, будто на это уходит половина дня. Оказалось, что тот споткнулся о торчащую во льду железку. При должном усердии можно ее извлечь, но хотите ли вы потратить на это остаток дня и сил?', 140),
(130, 'К барабанам прибавились какие-то потусторонние волынки. В вас начинает подниматься веселая злость.', 141),
(131, 'Под ногами вы замечаете странные ледяные наросты. Чем дальше вы продвигаетесь, тем тоньше и острее они становятся. Ближе к береговой линии они сходят на нет, но, чтобы обойти это препятствие, вам потребуется потратить около дня пути. Вы решаете…', 142),
(132, 'Повозившись немного, вы сооружаете из обрывков веревки и древесины что-то наподобие японских башмаков на деревянной подошве. Идти на этом тяжело, но острые шипы задорно хрустят у вас под ногами, и препятствие вы преодолеваете без особого труда.', 21),
(133, 'Бесконечный дождь перестал быть проклятием. Он стал широкой, закручивающейся спиралью, безбрежным морем для вас одного. Вы с трудом понимаете, где находитесь, вы не помните практически ничего из того, что вам пришлось пережить. Вы уже несколько дней лежите на сухой траве под теплым осенним солнцем, собираясь с последними силами для очередного бессмысленного марш-броска в никуда. Вполне возможно, вам удастся спастись, но черное небо, грязь и дождь останутся в вашей голове навсегда.', NULL),
(134, 'В найденной пещерке вам удается без проблем развести костер и даже кое-как высушить одежду.', 22),
(135, 'Вы преодолеваете болото и слышите голосок ручейка. Вы ускоряете шаг, но проходит довольно много времени, прежде чем тот становится громче. На исходе дня вы добираетесь до него, в очередной раз поражаясь обманчивости расстояний. «Здесь сопки шерстисты, как носорог, а расстояние забыло об измерении километром…» — вспоминаете вы стихи местного автора. Погрузив голову в ручей, вы долго пьете до ломоты в зубах холодную воду.', 143),
(136, 'Опасливо попробовав первую ягодку, вы убеждаетесь, что на вкус она - как обычная морошка. Наутро у вас, правда, трясутся руки, и всю ночь вы видели странные сны, но все это вы списываете на нечеловеческие условия, в которых вам приходится держать путь.', 96),
(137, 'Не сговариваясь, вы поднимаетесь со льда и идете навстречу свету. Странно, но, как только вы избрали это направление, усталость как рукой сняло. Впрочем, это касается только ваших тел, разум же, кажется, затуманился еще сильнее. В ваших головах пульсирует лишь одна мысль: если бы вы вдруг захотели повернуть, смогли бы вы? Свет притягивает все сильнее. Лед под ногами абсолютно прозрачен и лучится неземным светом. С ужасом вы понимаете, что его на самом деле излучают люминесцирующие, шевелящиеся глаза, изучающие вас с плотоядным интересом. Крик застревает у вас в глотках, последние остатки рассудка покидают вас... В следующий миг вы оказываетесь у давно потухшего костра. Мороз будто бы стал сильнее, но вам жарко, вас прошибает пот. Никто из вас не говорит с остальными о произошедшем, но все вы чувствуете, что что-то в вас изменилось навсегда...', 23),
(138, 'От сырости и ходьбы ваши ноги покрываются скверно выглядящими нарывами. Вам нужно передохнуть и дать ступням хоть как-то зажить...', 144),
(139, 'Терять единственную обувь на льду у вас нет никакого желания, и вы решаете перестраховаться. Впрочем, время в вопросах выживания для вас - отнюдь не союзник.', 97),
(140, 'С трудом отогревшись у костра, вы продолжаете свой путь. В вашем измученном мозгу крепнет пораженческая мысль, что вы заблудились и бредете куда-то вдоль трассы… Кажется, нужно что-то делать…', 145),
(141, 'Немногим позже вы находите в овраге небольшой ржавый коробок. Не без труда вы отковыриваете ржавую крышку и находите несколько бутылей с таблетками: без названий, но помеченных едва различимыми цветными бумажками. Определить, что это за лекарства, невозможно, как и то, сколько они пролежали под воздействием солнца и влаги. Впрочем, если вы едва стоите на ногах, можно и рискнуть…', 146),
(142, 'Дикий гон Вы выбираетесь на просторную опушку, со всех сторон окруженную лесом. Вдали слышится какой-то рокот, будто бы сходит огромный сель, или несется колоссальный массив воды... Вы замираете в ужасе, ожидая, что вас вот-вот сметет что-то огромное. Из чащи толпой выбегают звери, спасаясь от чего-то неведомого, и несутся они прямо на вас!', 98),
(143, 'Вы просыпаетесь очень рано. Как только вы открываете глаза, в вашей груди расправляет крылья надежда. Вы представляете, как рассказываете друзьям о своих приключениях и улыбаетесь. Все происходящее — очередное приключение, закаляющее характер и добавляющее страницы в гипотетические мемуары. Мысль о мемуарах так вас увлекает, что вы на какое-то время решительно не способны думать ни о чем другом.', 99),
(144, 'Очередной день пролетает незаметно. Однообразный пейзаж, медленно проплывающий мимо, мучает вас: кажется, будто вы топчитесь на месте. Когда эта чудовищная пытка закончится? Вы не знаете, и знать не можете. Короткая ночь заканчивается, и вы отправляетесь в путь.', 147),
(145, 'Вы вскакиваете, стоит вам заслышать подозрительный звук. В какой-то момент вы даже берете из костра горящую головешку и идете проверить кусты. Всю ночь вас мучает все нарастающий страх. Рассвет застает вас с широко открытыми глазами, в полубессознательном состоянии. Собрав вещи, вы продолжаете путь. Возможно, кого-то стоило оставить на стреме...', 100),
(146, 'Чувствуя себя совершенно по-идиотски, вы несколько раз пытаетесь воспроизвести увиденные движения. Получается у вас лишь отдаленное подобие увиденного, однако льющийся градом дождь вдруг превращается во вполне терпимую морось; впрочем, ненадолго. Это маленькое приключение приподнимает ваш боевой дух.', NULL),
(147, 'Чуть позже, уже под вечер, вы натыкаетесь на заброшенную военную часть. Вы решаете забраться в подвал одного из зданий в надежду найти хоть что-то, что сможет помочь вам добраться живыми до Трассы. Вместо этого вы обнаруживаете причудливый механизм, собранный из несочетаемых на первый взгляд компонентов. Неясно, от чего он питается, но, стоит вам зайти в комнату, как тот оживает и преграждает вам путь к выходу…', 101),
(148, 'Жара невероятная. Вас душат тучи гнуса, под ногами пружинят кочки. Иногда вы останавливаетесь, чтобы собрать морошки: та только-только начала наливаться оранжевым в обещании терпкого сока и сладкой мякоти. Вы все равно съедаете безвкусные зеленые ягоды.', 24),
(149, 'Ваш путь преграждает огромная, мутная, ревущая река. В обычное время она вполне может быть безобидным ручейком, но сейчас — паводок. Придется решать, как ее преодолеть...', 148),
(150, 'На коротком привале один из вас обнаруживает, что от влаги и плесени стенка его вещмешка почти расползлась… Вы не знаете, злиться, или смеяться, но ваш боевой дух поднялся.', 25),
(151, 'Куда вы шли? Зачем? Это вам больше не ведомо. Вы полностью отдались охоте, бегу, странным запахам леса. Через какое-то время вы учуяли какой-то новый след. Пройдя по нему, вы обнаружили странное двуногое существо. То начало кричать и махать руками. В руках у него была странная гладка палка. На мгновение вы почуяли резкий запах гари, а потом - побежали снова, но уже не чувствуя лап, куда-то в прозрачную крутящуюся даль потустороннего мира.', NULL),
(152, 'Немногим позже вы натыкаетесь на незамерзшую полынью. Похоже, нерпы используют ее, чтобы глотнуть кислорода, прежде чем спуститься под лед… Вы можете попробовать подкараулить одну из них. Для этого вам потребуется охотничье устройство. Вы решаете соорудить его из…', 149),
(153, 'Кажется, здесь была пищевая сода. Ничего не происходит.', 150),
(154, 'Вы находите каменистый склон с нависающими над головой монументальными останцами. Под ними более-менее сухо, и вы тут же сваливаетесь спать, разложив вещи сушиться... Кто-то из вас просыпается от запаха гари, и с ужасом принимается выбрасывать из разшалившегося костра обугленные ботинки и одежду. Потеря обуви приводит вас в отчаяние, ведь шансы выбраться и так невелики…', 102),
(155, 'К вечеру вы смотрите лишь под ноги, которые, кажется, медленно переставляются сами по себе. Подняв усталые глаза в причудливой рамке искусанных комарами век, вы резко останавливаетесь. Вдалеке теплится какой-то огонек… Может, охотники?! Вы судорожно сглатываете, делаете несколько шагов в направлении огонька… и мгновенно проваливаетесь в болотную жижу по щиколотку. В вашей голове зреет мучительное решение.', 151),
(156, 'С огромным трудом вам удается выковырять продолговатый предмет из толщи льда. Один из вас коснулся его рукой в рваной рукавице и сдавленно чертыхнулся. При ближайшем рассмотрении вы с удивлением понимаете, что это... оружие! Заржавелое и ломкое, но пару ударов оно выдержит. Вы закидываете находку в вещмешок и насколько можете торопливо разбиваете лагерь в «кармане» неподалеку.', 103),
(157, 'Вам не хватает продуктов, вы срезаете несколько кусков самого чистого на вид мяса.', 104),
(158, '«Припасы мне пригодятся»,, — решаешь ты, уверенно сворачивая с дороги. Ты осматриваешь бетонные коробки одну за другой, но не находишь ничего, кроме холодных полов, покрытых сантиметровым слоем рассыпающихся от прикосновения ржавых деталей. Похоже, здесь когда-то чинили машины, или что-то в этом роде. Впрочем, через какое-то время ты находишь ржавый сундучок.', 26),
(159, 'Вам надоело, что неумелый тунеядец напрасно тратит ценные ресурсы. Вы решаете его судьбу за него. Какое-то время он еще тащится за вами, причитая и завывая, пока не падает, сотрясаясь в отчаянном подобии плача. Слезы не текут: в его теле для них не хватает чего-то жизненно важного.', 105),
(160, 'Вы решаете не искушать судьбу. Кто знает, что может быть в этих бутылях. Оставив всё как есть, вы продолжаете свой нелегкий путь.', NULL),
(161, 'Вы тратите день и скудные остатки сил, чтобы склепать из дощечек и обрывок пеньки что-то, отдаленно напоминающее снегоступы. В долгосрочной перспективе это сослужит вам добрую службу, но доживете ли вы до этого момента?', 106),
(162, 'Идущему впереди вспоминается поговорка о спасении утопающего, и ваша группа продолжает путь до тех пор, пока все не обессиливают окончательно. Вместо организованного привала получается животное лежбище. Вас будит только боль в конечностях, но и то хорошо. Это значит, что они ещё не вконец обморозились.', 107),
(163, 'Вам не хочется нарушать прекрасную тишину. Вас окутывает сладостное оцепенение. Что, если простоять так вечность? Может, вы превратитесь в дерево? Вы не знаете.', 108),
(164, 'Ваши рефлексы ускоряются, однако организм работает на пределе, и любая рана может оказаться смертельной.', 109),
(165, 'Люди — это люди, а выглядит ваша группа жалко. Даже если это — «хищники», они вряд ли откажут в помощи. С этими мыслями вы уверенно приближаетесь к источнику звука. Трасса славится взаимовыручкой. Люди наверняка вам помогут. Вы продолжаете ломиться через кусты, молясь, чтобы спорщики не уехали прежде, чем вы успеете до них добраться. Вы слышите, как заводится двигатель, изо всех сил кричите и рветесь вперед, царапая лицо и руки. В итоге вы вываливаетесь на поляну прямо перед небольшим УАЗом, у которого стоят два мужика. Один из них испуганно матерится и поднимает карабин. Второй предупредительно вскрикивает, но тот уже нажал на курок. По счастливой случайности он промахивается, но одного из вас зацепило дробью.', 110),
(166, 'Вы замечаете что-то необычное. Склон одного из «карманов» очень пологий, почти не покрыт снегом и образует своеобразную лестницу из сыпучего гранита. Взбираться по нему — затея опасная, но может наверху будет лыжная тропа? Или, чего пуще, трасса? Выбор дается вам нелегко, но в конце концов вы решаете...', 152),
(167, 'Здоровье для вас всегда было в приоритете. Нужно подлечить нарывы, чтобы ситуация с ними не стала ещё хуже.', 111),
(168, 'Из дощечек и тряпок вы делаете примитивные очки: вырезаете в дощечках тонкие полосы и приматываете к голове. Очки постоянно съезжают, но серьезного ущерба глазам удается избежать. К сожалению, работа вышла хоть и топорная, но тонкая, и вы потратили на нее немало ресурсов.', 112),
(169, 'Истертые в кровь ноги уже перестали причинять вам боль. Вы двигаетесь как будто на автопилоте. В вашей голове только иногда появляются какие-то мысли, но и они сразу растворяются как в тумане. Тем временем сил в истощенных телах остаётся всё меньше. Кто-то из вас совсем обессилел. Бедняга то и дело спотыкается и постепенно отстаёт от общей группы.', 153),
(170, 'Темнеет, и вы из последних сил забредаете в «карман». Его склоны неприступны и покрыты снегом, но у подножья пробиваются ветки кустарника. Перед вами встает выбор, где разбить лагерь.', 154),
(171, 'На очередной неприметной развилке вы забираете влево, и скоро оказываетесь на огромной мшистой равнине. В сухое время она была бы райским местечком: прохладный ветерок, сухой лишайник под ногами, легко пружинящие кочки... Но сейчас, когда с неба льется и льется дождь, равнина превратилась в полосу препятствий: мох намок и раздулся, а ноги соскальзывают с мокрых кочек. Преодолеть равнину вам удается лишь к вечеру, и наградой за труды вам стала крохотная пещерка из-под огромного (по магаданским меркам) дерева-выворотня. Вы решаете…', 155),
(172, 'В этой банке было что-то нехорошее… Внезапно на вас нападают какие-то звери, и вы начинаете яростно обороняться.', 113),
(173, 'Вы вспоминаете старые байки про мстительные души детей, заводящие неосторожных путников в самую глубь зловонных топей. Все в вас протестует, но глупая легенда не дает покоя. Со слезами на глазах вы отворачиваетесь и продолжаете свой путь. Может, в том огоньке было ваше спасение?..', NULL),
(175, 'Это может быть ловушка. Вы решаете обойти вешки стороной.', NULL),
(176, 'С огромным трудом вы находите в себе силы простить незадачливого соратника. В сердце каждого из вас кипит ярость, но выхода она не находит: то ли из-за смертельной усталости, то ли из-за обостренной совести.', 27),
(177, 'Оставаться здесь опасно, думаете вы. Вдруг это укрытие служит домом одному из обитателей леса. Недолго думая ваша группа решает отправиться дальше. Возможно оно и к лучшему.', 114),
(178, 'Ночью вы просыпаетесь от чудовищной боли в районе шеи. С криком вы взвиваетесь на ноги, и в мягком свете полугасшего костра видите что-то маленькое и юркое, похожее на горностая, прыгающее туда-сюда по камням. Через какое-то время зверек атакует снова и вновь умудряется отгрызть из вас кусок мяса. Вы тщетно размахиваете руками, пытаясь попасть по нему или отогнать. Похоже, ваш единственный шанс - прибить его, пока он еще повис на вас.', 115),
(179, 'У самого склона сопки вы замечаете небольшой грот. Внутри него на удивление тепло, а по стенкам стекает пресный конденсат. Вы решаете заночевать в нем, и за ночь в поставленные фляги набирается немного воды.', 28),
(180, 'Белый медведь Вы с трудом перепрыгиваете с льдины на льдину, справа от вас - отвесные скалы. Вас прижало почти к самому берегу, и перспектива просто не перепрыгнуть очередную расселину становится все более вероятной. Завернув за очередной “карман”, вы нос к носу сталкиваетесь с огромным хищником: шкура его посерела и обвисла на ребрах. Неизвестно, сколько он голодал и сколько тысяч километров преодолел на сводящем с ума ледоходе. Медведь раскрывает алую пасть и бросается к вам, лишь ледяная пыль вылетает из-под когтей!', 116),
(181, 'Напившись, вы падаете на траву и долго лежите не шевелясь. Вы бы пролежали так до вечера, если бы не ужасное чувство голода. Пора идти.', 29),
(182, 'Если решаете сразиться с ним, готовьтесь к серьезному вызову.', 30),
(183, 'Вы просто выкрикиваете в лес случайный набор слов, пришедших на ум. Это одновременно и веселит, и немного устрашает: что, если вы, сами того не зная, сошли с ума?', NULL),
(184, 'Вы продолжаете идти, впереди уже виднеется громада перевала Подумай, названного так в предостережение беспечным путешественникам. Или вам лишь кажется? В попытке разогнать хищных зверей вы громко поете: об этом трюке вы слышали от бывалых охотников. Вы решаете спеть…', 156),
(185, 'Ночевать у крутых склонов кажется вам идеей небезопасной. Вы встаете лагерем в стороне от скалистой выемки и проводите ночь без приключений.', NULL),
(186, 'Возможно, все, что вы видели, было причудливым миражом или коллективной галлюцинацией. Вы не придаете случившемуся значения и продолжаете путь.', NULL),
(187, 'С огромным трудом вы пробираетесь сквозь снежные завалы. Как оказалось, идти вам все равно приходится вдоль обрыва, так как лесная чаща слева абсолютно непролазна. В итоге вы следуете причудливым изгибам береговой линии вместо того, чтобы двигаться по льду по прямой. Под вечер вы набредаете на узкое плато, покрытое кустарником и не очень глубоким снегом, местами окрашенным красным. Неподалеку вы обнаруживаете полуобглоданную заячью тушку: очевидно, вы спугнули трапезничавшего хищника. Тушка еще теплая, и вы кое-как делите мясо поровну. По крайней мере, сегодня вас ждет горячий, хоть и скудный ужин.', 31),
(188, 'Вы готовы поклясться, что видите между ветками стланика, стеной закрывающего одну из сторон тропинки, светлый просвет… Вы забираетесь в чащу, прыгаете по пружинистым веткам, нещадно цепляясь вещмешком за все сучья подряд. Через пару часов вы понимаете, что бурелом и не думает кончаться. Нужно идти назад.', 157),
(189, 'Вам встречается зияющая тёмная расщелина среди камней. Один из вас пробует найти внутри что-то полезное.', 32);
INSERT INTO effects (name, description, effect_type) VALUES
INSERT INTO endings (title_ending, end_descr, alt_question, scenario_id) VALUES
('Лучший исход', 'Вскоре ты поднимаешься по каменистому отвалу: коричневому и сыпучему. Перекатившись на спину, ты встаешь и тупо рассматриваешь километровый столбик. Через какое-то время тебя начинает разбирать дикий смех, ты падешь на колени  и долго  плачешь мучительными, бесслезными спазмами. Ты плачешь до тех пор, пока рядом не останавливается автомобиль, которого ты не замечаешь. Из ступора тебя выводит вопрос, который с давних времен стал визитной карточкой этой бесконечной северной дороги: — Вам помочь?', NULL, 1),
('Тяжелая утрата', 'Вы понимаете, что выжили не просто так. За каждого из вас, кто смог добраться до спасительной цивилизации, принесли жертву, заплатили цену в человеческую жизнь ваши покойные товарищи. Вы ликуете, но и понимаете, что теперь вам придется жить как-то иначе. Ведь за спиной у вас остались те, кто не дошли. И, похоже, вам придется жить и за них тоже.', NULL, 1),
('Безысходность', 'Лес постепенно стал вашим лучшим другом. Вы знаете все его секреты. Знаете, куда лучше не соваться. Где происходит что-то, что не под силу понять городским жителям. Лес стал вашей второй душой. Вы не можете найти ни одной причины возвращаться в город. Зато здесь, в лесу, вы стали чем-то большим. Однажды вы наткнулись на небольшой лагерь, где у костра грелись какие-то люди. Один рассказывал другому, что в этих краях завелся леший. Второй не верил, громко хохотал и матерился. Вы пожали плечами, развернули тропу, по которой они пришли, вспять, и молча растворились в прохладном воздухе.', NULL, 1),
('Лесной инстинкт', 'Куда вы шли? Зачем? Это вам больше не ведомо. Вы полностью отдались охоте, бегу, странным запахам леса. Через какое-то время вы учуяли какой-то новый след. Пройдя по нему, вы обнаружили странное двуногое существо. То начало кричать и махать руками. В руках у него была странная гладка палка. На мгновение вы почуяли резкий запах гари, а потом - побежали снова, но уже не чувствуя лап, куда-то в прозрачную крутящуюся даль потустороннего мира.', 'Вы слышали на своём пути голоса, и пошли на их зов?', 1),
